-- ----------------------------
-- Table structure for forum_user
-- ----------------------------
DROP TABLE IF EXISTS `forum_user`;
CREATE TABLE `forum_user` (
  `uid` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `nickname` varchar(50) NOT NULL COMMENT '昵称',
  `head` varchar(100) NOT NULL COMMENT '头像',
  `account` varchar(50) NOT NULL COMMENT '账号',
  `psword` char(32) NOT NULL COMMENT '密码',
  `add_time` int(11) unsigned NOT NULL COMMENT '注册时间',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态 1开启 0关闭 ',
  `is_admin` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '管理员 1是 0否',
  PRIMARY KEY (`uid`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='用户表';

